mapper.select:
SELECT 
	orders_number
FROM orders
WHERE users_usernumber = #{usersNumber}
 	AND uuid = #{uuid}

mapper.update:
UPDATE orders
SET result = #{result}
WHERE orders_number = #{ordersNumber}

UPDATE orders
SET tid = #{tid}
WHERE orders_number = #{ordersNumber}

UPDATE orders
SET result = #{result},
regdate = #{regDate}
WHERE uuid = #{uuid}

mapper.select:
SELECT tid
FROM orders
WHERE uuid = #{uuid}
